    box-sizing: border-box;
}

:after {
    content: "";
}

#section {
    position: relative;
    left: 100px;
    max-width: 640px;
}

h1 {
    color: white;
    margin: 80px 0 10px 0;
    font-size: 52px;
    font-family: 'Montserrat', sans-serif;
    text-transform: uppercase;
    text-shadow: 1px 1px 0px rgb(150, 17, 43), 2px 2px 0px rgb(150, 17, 43), 3px 3px 0px rgb(150, 17, 43), 4px 4px 0px rgb(150, 17, 43);
    line-height: 1.4;
}

h2 {
    color: white;
    font-size: 24px;
    font-family: 'Montserrat', sans-serif;
}

body {
    padding: 100px 0;
    background: rgb(15, 19, 20);
}

*, *:before, *:after {
    box-sizing: border-box;
}

:after {
    content: "";
}

nav {
    float: left;
    position: relative;
    top: 0;
    left: 0;
    background: transparent;
}

nav ul {
    text-align: center;
}

nav ul li {
    position: relative;
    width: 70px;
    cursor: pointer;
    background: rgb(150, 17, 43);
    text-transform: uppercase;
    transition: all .4s ease-out;
}

nav ul li:after {
    position: absolute;
    background: white;
    color: rgb(150, 17, 43);
    top: 0;
    left: 70px;
    width: 70px;
    height: 100%;
    opacity: .5;
    transform: perspective(400px) rotateY(90deg);
    transform-origin: 0 100%;
    transition: all .4s ease-out;
}

nav ul li:nth-child(1):after {
    content: "Home";
    line-height: 88px;
}

nav ul li:nth-child(2):after {
    content: "About";
    line-height: 88px;
}

nav ul li:nth-child(3):after {
    content: "Blog";
    line-height: 85px;
}

nav ul li:nth-child(4):after {
    content: "Say hi";
    line-height: 70px;
}

nav ul li:hover {
    transform: translateX(-70px);
}

nav ul li:hover:after {
    opacity: 1;
    transform: perspective(400px) rotateY(0deg) scale(1);
}

nav ul li>div {
    display: inline-block;
    padding: 25px 0;
    background: transparent;
}

nav ul li div {
    position: relative;
}

.roof {
    width: 0;
    height: 0;
    top: 2px;
    border-style: solid;
    border-width: 0 21px 15px 21px;
    border-color: transparent transparent #ffffff transparent;
}

.roof-edge {
    position: absolute;
    z-index: 20;
    left: -17px;
    top: 3px;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0 17px 12px 17px;
    border-color: transparent transparent rgb(150, 17, 43) transparent;
}

/*white triangle over red triangle*/

.roof-edge:after {
    position: absolute;
    left: -14.5px;
    top: 3px;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0 14.5px 10px 14.5px;
    border-color: transparent transparent white transparent;
}

.front {
    position: relative;
    top: 3px;
    width: 28.5px;
    height: 20px;
    margin: 0 auto;
    background: white;
}

/*door*/

.front:after {
    position: absolute;
    background: rgb(150, 17, 43);
    width: 11px;
    height: 13px;
    bottom: 0;
    left: 9px;
}

/*/// About me ////*/

.head {
    width: 32px;
    height: 35px;
    background: white;
    border-radius: 8px;
}

/*mouth*/

.head:after {
    width: 4px;
    height: 10px;
    background: white;
    position: absolute;
    border-radius: 4px 0 0 4px;
    top: 21px;
    left: 14px;
    transform: rotate(270deg);
}

.eyes {
    width: 8px;
    height: 5px;
    border-radius: 50%;
    position: absolute;
    top: 10px;
    left: 5px;
    background: rgb(150, 17, 43);
}

/*right eye*/

.eyes:after {
    width: 8px;
    height: 5px;
    border-radius: 50%;
    position: absolute;
    top: 0;
    left: 14px;
    background: rgb(150, 17, 43);
}

.beard {
    width: 32px;
    height: 17px;
    background: rgb(150, 17, 43);
    border: 2px solid white;
    position: absolute;
    bottom: 0;
    left: 0;
    border-radius: 0 0 8px 8px;
}

/*nose*/

.beard:after {
    position: absolute;
    top: -2px;
    left: 11px;
    background: white;
    width: 6px;
    height: 4px;
    border-left: 1px solid rgb(150, 17, 43);
    border-right: 1px solid rgb(150, 17, 43);
}

/*//work//*/

.paper {
    position: relative;
    height: 32px;
    width: 29px;
    background: white;
    border: 2px solid white;
}

/*window*/

.paper:after {
    position: absolute;
    top: 1px;
    left: 0;
    width: 25px;
    height: 29px;
    background: white;
    border-top: 4px solid rgb(150, 17, 43);
}

.lines {
    position: absolute;
    top: 36px;
    left: 5px;
    width: 11px;
    box-shadow: 0 0 0 1px rgb(150, 17, 43);
}

.lines:after {
    position: absolute;
    top: 4px;
    left: 3px;
    width: 14px;
    box-shadow: 0 0 0 1px rgb(150, 17, 43);
}

.lines:nth-child(2) {
    position: absolute;
    top: 44px;
    left: 8px;
    width: 11px;
}

.lines:nth-child(2):after {
    position: absolute;
    top: 4px;
    left: -3px;
    width: 11px;
}

.lines:nth-child(3) {
    position: absolute;
    top: 52px;
    left: 8px;
    width: 14px;
}

.lines:nth-child(3):after {
    display: none;
}

/*//mail //*/

.mail-base {
    position: relative;
    width: 32px;
    height: 18px;
    background: white;
}

.mail-top {
    position: absolute;
    z-index: 20;
    left: 0;
    top: 0;
    width: 0;
    height: 0;
    transform: rotate(180deg);
    border-style: solid;
    border-width: 0 16px 11px 16px;
    border-color: transparent transparent rgb(150, 17, 43)transparent;
}

.mail-top:after {
    position: absolute;
    z-index: 20;
    left: -16px;
    top: 3px;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0 16px 9px 16px;
    border-color: transparent transparent white transparent;
}

.lastdance {
    color: #ffffff;
    background: url(https://2.bp.blogspot.com/-QbWUhjjb_Wg/XBYgtX9S-sI/AAAAAAAAAkk/At_kWRFe4ooFJcFQKfgoeT05sFVBgWv7ACLcBGAs/s1600/wade.jpg);
    background-repeat: no-repeat;
    background-position: center;
    height: 1200px;
    margin-top: 10%;
}

.footer {
    color: white;
    margin-right: 10%;
    font-size: 44px;
    text-align: center;
    width: 100%;
}
/* Facebook */
.fa-facebook {
    background: #3B5998;
    color: white;
  }

  /* Twitter */
  .fa-twitter {
    background: #55ACEE;
    color: white;
  }
  .fa-instagram {
      background: orange;
      color: white;
